package Leetcode;

import java.util.*;

/**
 * 438. 找到字符串中所有字母异位词
 */
public class LeetCode438 {
    public static void main(String[] args) {
        LeetCode438 leetCode438 = new LeetCode438();
        List<Integer> anagrams = leetCode438.findAnagrams2("",
                "");
        System.out.println("anagrams = " + anagrams);
    }

    /**
     * 此方法leetcode超时。。。
     *
     * @param s
     * @param p
     * @return
     */
    public List<Integer> findAnagrams(String s, String p) {
        List<Integer> res = new ArrayList<>();

        char[] chars = p.toCharArray();
        int size = p.length();

        char[] sChars = s.toCharArray();
        for (int i = 0; i < sChars.length; i++) {
            //先处理待比较字符串放入map中
            HashMap<Character, Integer> map = new HashMap();
            for (char aChar : chars) {
                Integer integer = map.get(aChar);
                map.put(aChar, integer == null ? 1 : integer + 1);
            }

            char sChar = sChars[i];
            //如果剩余字符串长度不足待比较字符串长度，直接退出
            if (sChars.length - i < size) {
                break;
            }
            //如果待比较数组不含该字符，则直接下一位
            if (!map.containsKey(sChar)) {
                continue;
            }
            //在遍历比较输入字符串
            for (int j = 0; j < size; j++) {
                char charj = sChars[i + j];
                if (map.containsKey(charj) && map.get(charj) > 0) {
                    map.put(charj, map.get(charj) - 1);
                    //判断map的value是否都为0 都为0 说明是ok的加入集合
                    if (map.get(charj).equals(0) && map.values().stream().allMatch(n -> n == 0)) {
                        Collection<Integer> values = map.values();
                        res.add(i);
                    }

                } else {
                    break;
                }
            }

        }
        return res;
    }

    /**
     * 能通过测试了 但是速度不太行
     *
     * @param s
     * @param p
     * @return
     */
    public List<Integer> findAnagrams1(String s, String p) {
        List<Integer> res = new ArrayList<>();

        char[] pChars = p.toCharArray();
        int size = p.length();
        int zeroCount = size;

        char[] sChars = s.toCharArray();
        for (int i = 0; i < sChars.length; i++) {
            //如果剩余字符串长度不足待比较字符串长度，直接退出
            if (sChars.length - i < size) {
                break;
            }
            //先处理待比较字符串放入字典中
            int[] array = new int[26];
            for (char pChar : pChars) {
                int i1 = pChar - 'a';
                array[i1]++;
            }
            zeroCount = size;
            //如果待比较数组不含该字符，则直接下一位
            char sChar = sChars[i];
            if (array[sChar - 'a'] == 0) {
                continue;
            }

            //在遍历比较输入字符串
            for (int j = 0; j < size; j++) {
                int i2 = sChars[i + j] - 'a';
                if (array[i2] != 0) {
                    array[i2]--;
                    //判断map的value是否都为0 都为0 说明是ok的加入集合
                    if (array[i2] == 0 && Arrays.stream(array).allMatch(n -> n == 0)) {
                        zeroCount--;
                        res.add(i);
                    }
                } else {
                    break;
                }
            }

        }
        return res;
    }

    public List<Integer> findAnagrams2(String s, String p) {
        List<Integer> res = new ArrayList<>();

        char[] pChars = p.toCharArray();
        int size = p.length();

        char[] sChars = s.toCharArray();
        for (int i = 0; i < sChars.length; i++) {
            //如果剩余字符串长度不足待比较字符串长度，直接退出
            if (sChars.length - i < size) {
                break;
            }
            //先处理待比较字符串放入字典中
            int[] array = new int[26];
            for (char pChar : pChars) {
                int i1 = pChar - 'a';
                array[i1]++;
            }
            int zeroCount = size;
            //如果待比较数组不含该字符，则直接下一位
            char sChar = sChars[i];
            if (array[sChar - 'a'] == 0) {
                continue;
            }

            //在遍历比较输入字符串
            for (int j = 0; j < size; j++) {
                int i2 = sChars[i + j] - 'a';
                if (array[i2] != 0) {
                    array[i2]--;

                    if (array[i2] == 0) {
                        zeroCount--;
                        if (zeroCount == 0) {
                            res.add(i);
                        }

                    }
                } else {
                    break;
                }
            }

        }
        return res;
    }


}
